export const totalAmenities = amenities.length;

export const mapAmenities = (selectedAmenities: (boolean | undefined)[] | undefined) => {
  const finalAmenities: boolean[] = selectedAmenities ?? Array(amenities.length).fill(false);
  return finalAmenities
    .map((isSelected: boolean, index: number) => (isSelected ? amenities[index].label : null))
    .filter((label): label is string => label !== null);
};

export const getIconByLabel = (label: string) => {
  const amenity = amenities.find(item => item.label === label);
  return amenity ? amenity.icon : <BoltIcon className="text-primary w-4 h-4" />;
};

export const getSelectedAmenities = (selectedAmenityNames: string[]): boolean[] =>
  amenities.map(amenity => selectedAmenityNames.includes(amenity.label));
